/// Each token has the following payload:
///
///     {
///        "cool" : true
///     }
private struct TokenPayload: JWTPayload {
    var cool: BoolClaim

    func verify(using _: some JWTAlgorithm) throws {
        if !cool.value {
            throw JWTError.claimVerificationFailure(failedClaim: self.cool, reason: "not cool")
        }
    }
}
